<span class="line">        <span class="token keyword">var</span> odelete <span class="token operator">=</span> document<span class="token punctuation">.</span><span class="token function">querySelector</span><span class="token punctuation">(</span><span class="token string">&quot;#delete&quot;</span><span class="token punctuation">)</span></span>
