<span class="line"><span class="token keyword">var</span> allList <span class="token operator">=</span> document<span class="token punctuation">.</span><span class="token function">querySelectorAll</span><span class="token punctuation">(</span><span class="token string">&quot;.wrapper&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
