<span class="line">        <span class="token keyword">this</span><span class="token punctuation">.</span>isLoggedInSubject<span class="token punctuation">.</span><span class="token function">next</span><span class="token punctuation">(</span><span class="token boolean">true</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
