<span class="line">    <span class="token keyword">const</span> myURL <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">URL</span><span class="token punctuation">(</span>req<span class="token punctuation">.</span>url<span class="token punctuation">,</span><span class="token string">&quot;http://127.0.0.1:3000&quot;</span><span class="token punctuation">)</span></span>
