<span class="line"></span>
<span class="line">        <span class="token comment">// 鼠标按下</span></span>
<span class="line">        <span class="token keyword">function</span> <span class="token function">down</span><span class="token punctuation">(</span><span class="token parameter">evt</span><span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">            isDown <span class="token operator">=</span> <span class="token boolean">true</span></span>
<span class="line">            <span class="token keyword">const</span> <span class="token punctuation">{</span> x<span class="token punctuation">,</span> y <span class="token punctuation">}</span> <span class="token operator">=</span> <span class="token function">getPos</span><span class="token punctuation">(</span>evt<span class="token punctuation">)</span></span>
