<span class="line">        <span class="token keyword">var</span> option <span class="token operator">=</span> <span class="token punctuation">{</span></span>
<span class="line">            <span class="token literal-property property">geo</span><span class="token operator">:</span> <span class="token punctuation">{</span></span>
<span class="line">                <span class="token literal-property property">type</span><span class="token operator">:</span> <span class="token string">"map"</span><span class="token punctuation">,</span></span>
<span class="line">                <span class="token literal-property property">map</span><span class="token operator">:</span> <span class="token string">"chinaMap"</span><span class="token punctuation">,</span> <span class="token comment">//chinaMap需要和registerMap中的第一个参数保持一致</span></span>
<span class="line">                <span class="token literal-property property">roam</span><span class="token operator">:</span> <span class="token boolean">true</span><span class="token punctuation">,</span> <span class="token comment">//设置允许缩放以及拖动的效果</span></span>
<span class="line">                <span class="token literal-property property">label</span><span class="token operator">:</span> <span class="token punctuation">{</span></span>
<span class="line">                    <span class="token literal-property property">show</span><span class="token operator">:</span> <span class="token boolean">true</span><span class="token punctuation">,</span></span>
<span class="line">                <span class="token punctuation">}</span><span class="token punctuation">,</span></span>
<span class="line">            <span class="token punctuation">}</span><span class="token punctuation">,</span></span>
<span class="line">            <span class="token literal-property property">series</span><span class="token operator">:</span> <span class="token punctuation">[</span></span>
<span class="line">                <span class="token punctuation">{</span></span>
<span class="line">                    <span class="token literal-property property">data</span><span class="token operator">:</span> airData<span class="token punctuation">,</span></span>
<span class="line">                    <span class="token literal-property property">geoIndex</span><span class="token operator">:</span> <span class="token number">0</span><span class="token punctuation">,</span> <span class="token comment">//将空气质量的数据和第0个geo配置关联在一起</span></span>
<span class="line">                    <span class="token literal-property property">type</span><span class="token operator">:</span> <span class="token string">"map"</span><span class="token punctuation">,</span></span>
<span class="line">                <span class="token punctuation">}</span><span class="token punctuation">,</span></span>
<span class="line">                <span class="token punctuation">{</span></span>
<span class="line">                    <span class="token literal-property property">data</span><span class="token operator">:</span> scatterData<span class="token punctuation">,</span> <span class="token comment">//配置散点的坐标数据</span></span>
<span class="line">                    <span class="token literal-property property">type</span><span class="token operator">:</span> <span class="token string">"effectScatter"</span><span class="token punctuation">,</span></span>
<span class="line">                    <span class="token literal-property property">coordinateSystem</span><span class="token operator">:</span> <span class="token string">"geo"</span><span class="token punctuation">,</span> <span class="token comment">//指明散点图的坐标系统 geo的坐标系统</span></span>
