<span class="line">            <span class="token keyword">let</span> image1 <span class="token operator">=</span> <span class="token keyword">await</span> <span class="token function">loadImage</span><span class="token punctuation">(</span><span class="token string">"../assets/img/001.jpg"</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
