<span class="line"></span>
<span class="line">        <span class="token comment">// 手动调整</span></span>
<span class="line">        btn2<span class="token punctuation">.</span><span class="token function-variable function">onclick</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span></span>
<span class="line">            <span class="token comment">// 获取当前宽度</span></span>
<span class="line">            <span class="token keyword">var</span> currentWidth <span class="token operator">=</span> <span class="token function">parseInt</span><span class="token punctuation">(</span>window<span class="token punctuation">.</span><span class="token function">getComputedStyle</span><span class="token punctuation">(</span>divBox<span class="token punctuation">)</span><span class="token punctuation">.</span>width<span class="token punctuation">,</span> <span class="token number">10</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">            <span class="token comment">// 获取当前高度</span></span>
<span class="line">            <span class="token keyword">var</span> currentHeight <span class="token operator">=</span> <span class="token function">parseInt</span><span class="token punctuation">(</span>window<span class="token punctuation">.</span><span class="token function">getComputedStyle</span><span class="token punctuation">(</span>divBox<span class="token punctuation">)</span><span class="token punctuation">.</span>height<span class="token punctuation">,</span> <span class="token number">10</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">            <span class="token keyword">if</span> <span class="token punctuation">(</span>divBox<span class="token punctuation">.</span>clientWidth <span class="token operator">></span> <span class="token number">300</span><span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">                <span class="token keyword">var</span> newWidth <span class="token operator">=</span> currentWidth <span class="token operator">-</span> <span class="token number">30</span><span class="token punctuation">;</span> <span class="token comment">// 减少30像素</span></span>
<span class="line">                divBox<span class="token punctuation">.</span>style<span class="token punctuation">.</span>width <span class="token operator">=</span> newWidth <span class="token operator">+</span> <span class="token string">"px"</span><span class="token punctuation">;</span> <span class="token comment">// 设置新的宽度</span></span>
<span class="line"></span>
<span class="line">                <span class="token keyword">var</span> newHeight <span class="token operator">=</span> currentHeight <span class="token operator">-</span> <span class="token number">20</span><span class="token punctuation">;</span> <span class="token comment">// 减少20像素</span></span>
<span class="line">                divBox<span class="token punctuation">.</span>style<span class="token punctuation">.</span>height <span class="token operator">=</span> newHeight <span class="token operator">+</span> <span class="token string">"px"</span><span class="token punctuation">;</span> <span class="token comment">// 设置新的高度</span></span>
<span class="line"></span>
<span class="line">                mCharts<span class="token punctuation">.</span><span class="token function">resize</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">            <span class="token punctuation">}</span></span>
<span class="line">        <span class="token punctuation">}</span><span class="token punctuation">;</span></span>
