<span class="line">        <span class="token comment">// 绘制圆角矩形</span></span>
<span class="line">        <span class="token keyword">const</span> roundedRectangle <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">PIXI<span class="token punctuation">.</span>Graphics</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">        roundedRectangle<span class="token punctuation">.</span><span class="token function">beginFill</span><span class="token punctuation">(</span><span class="token number">0x66ccff</span><span class="token punctuation">,</span> <span class="token number">0.9</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">        <span class="token comment">// 绘制圆角矩形 </span></span>
<span class="line">        <span class="token comment">// 第一个参数x坐标，</span></span>
<span class="line">        <span class="token comment">// 第二个参数y轴坐标，</span></span>
<span class="line">        <span class="token comment">// 第三个参数是宽度，</span></span>
<span class="line">        <span class="token comment">// 第四个参数是高度，</span></span>
<span class="line">        <span class="token comment">// 第五个参数圆角半径</span></span>
<span class="line">        roundedRectangle<span class="token punctuation">.</span><span class="token function">drawRoundedRect</span><span class="token punctuation">(</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">144</span><span class="token punctuation">,</span> <span class="token number">64</span><span class="token punctuation">,</span> <span class="token number">10</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">        roundedRectangle<span class="token punctuation">.</span><span class="token function">endFill</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">        roundedRectangle<span class="token punctuation">.</span>position<span class="token punctuation">.</span><span class="token function">set</span><span class="token punctuation">(</span><span class="token number">200</span><span class="token punctuation">,</span> <span class="token number">100</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
