<span class="line"><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>head</span><span class="token punctuation">></span></span></span>
<span class="line"><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>body</span><span class="token punctuation">></span></span></span>
<span class="line">    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>script</span> <span class="token attr-name">type</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">"</span>module<span class="token punctuation">"</span></span><span class="token punctuation">></span></span><span class="token script"><span class="token language-javascript"></span>
<span class="line">        <span class="token keyword">import</span> <span class="token punctuation">{</span> Application<span class="token punctuation">,</span> Assets<span class="token punctuation">,</span> Sprite<span class="token punctuation">,</span> Rectangle <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">"../js/pixi.mjs"</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">        <span class="token comment">//使用立即执行函数进行调用</span></span>
<span class="line">        <span class="token punctuation">(</span><span class="token keyword">async</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span></span>
<span class="line">            <span class="token comment">// 创建一个新的应用程序</span></span>
<span class="line">            <span class="token keyword">const</span> app <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Application</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">            <span class="token comment">// 初始化应用程序</span></span>
<span class="line">            <span class="token keyword">await</span> app<span class="token punctuation">.</span><span class="token function">init</span><span class="token punctuation">(</span><span class="token punctuation">{</span></span>
<span class="line">                <span class="token literal-property property">width</span><span class="token operator">:</span> <span class="token number">600</span><span class="token punctuation">,</span></span>
<span class="line">                <span class="token literal-property property">height</span><span class="token operator">:</span> <span class="token number">400</span><span class="token punctuation">,</span></span>
<span class="line">            <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">            <span class="token comment">// 将应用程序画布添加到到文档正文</span></span>
<span class="line">            document<span class="token punctuation">.</span>body<span class="token punctuation">.</span><span class="token function">appendChild</span><span class="token punctuation">(</span>app<span class="token punctuation">.</span>canvas<span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">            <span class="token comment">// 加载图片到纹理</span></span>
<span class="line">            <span class="token keyword">const</span> texture <span class="token operator">=</span> <span class="token keyword">await</span> Assets<span class="token punctuation">.</span><span class="token function">load</span><span class="token punctuation">(</span><span class="token string">"../assets/images/eggHead.png"</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">            <span class="token comment">// 创建一个数组来存储外星人</span></span>
<span class="line">            <span class="token keyword">const</span> aliens <span class="token operator">=</span> <span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">            <span class="token keyword">const</span> totalDudes <span class="token operator">=</span> <span class="token number">20</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">            <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">let</span> i <span class="token operator">=</span> <span class="token number">0</span><span class="token punctuation">;</span> i <span class="token operator">&lt;</span> totalDudes<span class="token punctuation">;</span> i<span class="token operator">++</span><span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">                <span class="token comment">// 创建一个新的外星精灵</span></span>
<span class="line">                <span class="token keyword">const</span> dude <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Sprite</span><span class="token punctuation">(</span>texture<span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">                <span class="token comment">// 设置锚点，使纹理以角色为中心</span></span>
<span class="line">                dude<span class="token punctuation">.</span>anchor<span class="token punctuation">.</span><span class="token function">set</span><span class="token punctuation">(</span><span class="token number">0.5</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">                <span class="token comment">// 为这家伙设定一个随机比例——没有必要他们都是一样的大小！</span></span>
<span class="line">                dude<span class="token punctuation">.</span>scale<span class="token punctuation">.</span><span class="token function">set</span><span class="token punctuation">(</span><span class="token number">0.5</span> <span class="token operator">+</span> Math<span class="token punctuation">.</span><span class="token function">random</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">*</span> <span class="token number">0.3</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">                <span class="token comment">// 最后，让我们把这家伙放在一个随机的位置。。</span></span>
<span class="line">                dude<span class="token punctuation">.</span>x <span class="token operator">=</span> Math<span class="token punctuation">.</span><span class="token function">random</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">*</span> app<span class="token punctuation">.</span>screen<span class="token punctuation">.</span>width<span class="token punctuation">;</span></span>
<span class="line">                dude<span class="token punctuation">.</span>y <span class="token operator">=</span> Math<span class="token punctuation">.</span><span class="token function">random</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">*</span> app<span class="token punctuation">.</span>screen<span class="token punctuation">.</span>height<span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">                <span class="token comment">//设置随机的颜色 白色为最大值  随机值0-1之间 就包含所有的颜色</span></span>
<span class="line">                dude<span class="token punctuation">.</span>tint <span class="token operator">=</span> Math<span class="token punctuation">.</span><span class="token function">random</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">*</span> <span class="token number">0xffffff</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">                <span class="token comment">// 创建一些额外的属性来控制移动：---------------------</span></span>
<span class="line"></span>
<span class="line">                <span class="token comment">// 以弧度为单位创建随机方向。</span></span>
<span class="line">                <span class="token comment">// 这是一个介于0和PI*2之间的数字，相当于0-360度</span></span>
<span class="line">                dude<span class="token punctuation">.</span>direction <span class="token operator">=</span> Math<span class="token punctuation">.</span><span class="token function">random</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">*</span> Math<span class="token punctuation">.</span><span class="token constant">PI</span> <span class="token operator">*</span> <span class="token number">2</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">                <span class="token comment">// 这个数字将用于随着时间的推移修改该 dube 的方向</span></span>
<span class="line">                dude<span class="token punctuation">.</span>turningSpeed <span class="token operator">=</span> Math<span class="token punctuation">.</span><span class="token function">random</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">-</span> <span class="token number">0.8</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">                <span class="token comment">// 为2-4之间的家伙创建一个随机速度</span></span>
<span class="line">                dude<span class="token punctuation">.</span>speed <span class="token operator">=</span> <span class="token number">2</span> <span class="token operator">+</span> Math<span class="token punctuation">.</span><span class="token function">random</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">*</span> <span class="token number">2</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">                <span class="token comment">// 最后，我们把这家伙推到外星人阵列中，这样以后就可以很容易地访问它</span></span>
<span class="line">                aliens<span class="token punctuation">.</span><span class="token function">push</span><span class="token punctuation">(</span>dude<span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">                app<span class="token punctuation">.</span>stage<span class="token punctuation">.</span><span class="token function">addChild</span><span class="token punctuation">(</span>dude<span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">            <span class="token punctuation">}</span></span>
<span class="line"></span>
<span class="line">            <span class="token comment">// 为小家伙创建一个边界框 屏幕外各多一百距离</span></span>
<span class="line">            <span class="token keyword">const</span> dudeBoundsPadding <span class="token operator">=</span> <span class="token number">100</span><span class="token punctuation">;</span></span>
<span class="line">            <span class="token keyword">const</span> dudeBounds <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Rectangle</span><span class="token punctuation">(</span></span>
<span class="line">                <span class="token operator">-</span>dudeBoundsPadding<span class="token punctuation">,</span> </span>
<span class="line">                <span class="token operator">-</span>dudeBoundsPadding<span class="token punctuation">,</span> </span>
<span class="line">                app<span class="token punctuation">.</span>screen<span class="token punctuation">.</span>width <span class="token operator">+</span> dudeBoundsPadding <span class="token operator">*</span> <span class="token number">2</span><span class="token punctuation">,</span> </span>
<span class="line">                app<span class="token punctuation">.</span>screen<span class="token punctuation">.</span>height <span class="token operator">+</span> dudeBoundsPadding <span class="token operator">*</span> <span class="token number">2</span></span>
<span class="line">            <span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">            app<span class="token punctuation">.</span>ticker<span class="token punctuation">.</span><span class="token function">add</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span></span>
<span class="line">                <span class="token comment">// 遍历这些家伙并更新他们的位置</span></span>
<span class="line">                <span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">let</span> i <span class="token operator">=</span> <span class="token number">0</span><span class="token punctuation">;</span> i <span class="token operator">&lt;</span> aliens<span class="token punctuation">.</span>length<span class="token punctuation">;</span> i<span class="token operator">++</span><span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">                    <span class="token keyword">const</span> dude <span class="token operator">=</span> aliens<span class="token punctuation">[</span>i<span class="token punctuation">]</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">                    dude<span class="token punctuation">.</span>direction <span class="token operator">+=</span> dude<span class="token punctuation">.</span>turningSpeed <span class="token operator">*</span> <span class="token number">0.01</span><span class="token punctuation">;</span></span>
<span class="line">                    <span class="token comment">// 正弦是 -1到1 x轴会一直变化</span></span>
<span class="line">                    dude<span class="token punctuation">.</span>x <span class="token operator">+=</span> Math<span class="token punctuation">.</span><span class="token function">sin</span><span class="token punctuation">(</span>dude<span class="token punctuation">.</span>direction<span class="token punctuation">)</span> <span class="token operator">*</span> dude<span class="token punctuation">.</span>speed<span class="token punctuation">;</span></span>
<span class="line">                    <span class="token comment">// 余弦是 -1到1 y轴也会一直变化  两个结合就变成有幅度的移动，且方向不确定</span></span>
<span class="line">                    dude<span class="token punctuation">.</span>y <span class="token operator">+=</span> Math<span class="token punctuation">.</span><span class="token function">cos</span><span class="token punctuation">(</span>dude<span class="token punctuation">.</span>direction<span class="token punctuation">)</span> <span class="token operator">*</span> dude<span class="token punctuation">.</span>speed<span class="token punctuation">;</span></span>
<span class="line">                    <span class="token comment">// 对象自身的旋转 不会一直很小</span></span>
<span class="line">                    dude<span class="token punctuation">.</span>rotation <span class="token operator">=</span> <span class="token operator">-</span>dude<span class="token punctuation">.</span>direction <span class="token operator">-</span> Math<span class="token punctuation">.</span><span class="token constant">PI</span> <span class="token operator">/</span> <span class="token number">2</span><span class="token punctuation">;</span></span>
<span class="line"></span>
<span class="line">                    <span class="token comment">// 通过测试他们的界限来限制他们的位置。。。</span></span>
<span class="line">                    <span class="token comment">// 1.当x轴 超过左边的时候 当前值x把宽度给加上</span></span>
<span class="line">                    <span class="token comment">// 2.当x轴 超过右边的时候 将宽度进行减去</span></span>
<span class="line">                    <span class="token keyword">if</span> <span class="token punctuation">(</span>dude<span class="token punctuation">.</span>x <span class="token operator">&lt;</span> dudeBounds<span class="token punctuation">.</span>x<span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">                        dude<span class="token punctuation">.</span>x <span class="token operator">+=</span> dudeBounds<span class="token punctuation">.</span>width<span class="token punctuation">;</span></span>
<span class="line">                    <span class="token punctuation">}</span> <span class="token keyword">else</span> <span class="token keyword">if</span> <span class="token punctuation">(</span>dude<span class="token punctuation">.</span>x <span class="token operator">></span> dudeBounds<span class="token punctuation">.</span>x <span class="token operator">+</span> dudeBounds<span class="token punctuation">.</span>width<span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">                        dude<span class="token punctuation">.</span>x <span class="token operator">-=</span> dudeBounds<span class="token punctuation">.</span>width<span class="token punctuation">;</span></span>
<span class="line">                    <span class="token punctuation">}</span></span>
<span class="line"></span>
<span class="line">                    <span class="token comment">// 与x轴相同的道理</span></span>
<span class="line">                    <span class="token keyword">if</span> <span class="token punctuation">(</span>dude<span class="token punctuation">.</span>y <span class="token operator">&lt;</span> dudeBounds<span class="token punctuation">.</span>y<span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">                        dude<span class="token punctuation">.</span>y <span class="token operator">+=</span> dudeBounds<span class="token punctuation">.</span>height<span class="token punctuation">;</span></span>
<span class="line">                    <span class="token punctuation">}</span> <span class="token keyword">else</span> <span class="token keyword">if</span> <span class="token punctuation">(</span>dude<span class="token punctuation">.</span>y <span class="token operator">></span> dudeBounds<span class="token punctuation">.</span>y <span class="token operator">+</span> dudeBounds<span class="token punctuation">.</span>height<span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">                        dude<span class="token punctuation">.</span>y <span class="token operator">-=</span> dudeBounds<span class="token punctuation">.</span>height<span class="token punctuation">;</span></span>
<span class="line">                    <span class="token punctuation">}</span></span>
<span class="line">                <span class="token punctuation">}</span></span>
<span class="line">            <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">        <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">    </span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>script</span><span class="token punctuation">></span></span></span>
<span class="line"><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>body</span><span class="token punctuation">></span></span></span>
<span class="line"><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>html</span><span class="token punctuation">></span></span></span>
<span class="line"></span></code></pre>
