<span class="line">        <span class="token selector">.night-wrap</span> <span class="token punctuation">{</span></span>
<span class="line">            <span class="token property">width</span><span class="token punctuation">:</span> 500px<span class="token punctuation">;</span></span>
<span class="line">            <span class="token property">height</span><span class="token punctuation">:</span> 660px<span class="token punctuation">;</span></span>
<span class="line">            <span class="token property">background</span><span class="token punctuation">:</span> #04191c<span class="token punctuation">;</span></span>
<span class="line">            <span class="token property">background-position</span><span class="token punctuation">:</span> 0 160px<span class="token punctuation">;</span></span>
<span class="line">        <span class="token punctuation">}</span></span>
<span class="line"></span>
<span class="line">        <span class="token selector">.ripple</span> <span class="token punctuation">{</span></span>
