<span class="line">      <span class="token keyword">const</span> keyArr <span class="token operator">=</span> <span class="token punctuation">[</span><span class="token string">"ArrowUp"</span><span class="token punctuation">,</span> <span class="token string">"ArrowDown"</span><span class="token punctuation">,</span> <span class="token string">"ArrowLeft"</span><span class="token punctuation">,</span> <span class="token string">"ArrowRight"</span><span class="token punctuation">]</span><span class="token punctuation">;</span></span>
<span class="line">      <span class="token comment">//创建一个对象</span></span>
<span class="line">      <span class="token keyword">const</span> reObj <span class="token operator">=</span> <span class="token punctuation">{</span></span>
<span class="line">        <span class="token literal-property property">ArrowUp</span><span class="token operator">:</span> <span class="token string">"ArrowDown"</span><span class="token punctuation">,</span></span>
<span class="line">        <span class="token literal-property property">ArrowDown</span><span class="token operator">:</span> <span class="token string">"ArrowUp"</span><span class="token punctuation">,</span></span>
<span class="line">        <span class="token literal-property property">ArrowLeft</span><span class="token operator">:</span> <span class="token string">"ArrowRight"</span><span class="token punctuation">,</span></span>
<span class="line">        <span class="token literal-property property">ArrowRight</span><span class="token operator">:</span> <span class="token string">"ArrowLeft"</span><span class="token punctuation">,</span></span>
<span class="line">      <span class="token punctuation">}</span><span class="token punctuation">;</span></span>
<span class="line">      <span class="token comment">/* </span>
<span class="line">        游戏禁止掉头</span>
