<span class="line">      document<span class="token punctuation">.</span><span class="token function">addEventListener</span><span class="token punctuation">(</span><span class="token string">"keydown"</span><span class="token punctuation">,</span> <span class="token punctuation">(</span><span class="token parameter">event</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token punctuation">{</span></span>
<span class="line">        <span class="token keyword">if</span> <span class="token punctuation">(</span>keyActive <span class="token operator">&amp;&amp;</span> keyArr<span class="token punctuation">.</span><span class="token function">includes</span><span class="token punctuation">(</span>event<span class="token punctuation">.</span>key<span class="token punctuation">)</span><span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">          <span class="token keyword">if</span> <span class="token punctuation">(</span>snakes<span class="token punctuation">.</span>length <span class="token operator">&lt;</span> <span class="token number">2</span> <span class="token operator">||</span> reObj<span class="token punctuation">[</span>dir<span class="token punctuation">]</span> <span class="token operator">!==</span> event<span class="token punctuation">.</span>key<span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">            <span class="token comment">// 设置方向</span></span>
<span class="line">            dir <span class="token operator">=</span> event<span class="token punctuation">.</span>key<span class="token punctuation">;</span></span>
<span class="line">            keyActive <span class="token operator">=</span> <span class="token boolean">false</span><span class="token punctuation">;</span></span>
<span class="line">          <span class="token punctuation">}</span></span>
<span class="line">        <span class="token punctuation">}</span></span>
<span class="line">      <span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">      <span class="token comment">/* </span>
<span class="line">        要使得身体可以和头一起移动，只需要在蛇移动时，变化蛇尾巴的位置</span>
<span class="line">      </span>
<span class="line">      */</span></span>
<span class="line">      <span class="token function">setTimeout</span><span class="token punctuation">(</span><span class="token keyword">function</span> <span class="token function">move</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">        <span class="token comment">//获取蛇头</span></span>
<span class="line">        <span class="token keyword">const</span> head <span class="token operator">=</span> snakes<span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span><span class="token punctuation">;</span></span>
<span class="line">        <span class="token comment">//获取蛇头的坐标</span></span>
<span class="line">        <span class="token keyword">let</span> x <span class="token operator">=</span> head<span class="token punctuation">.</span>offsetLeft<span class="token punctuation">;</span></span>
<span class="line">        <span class="token keyword">let</span> y <span class="token operator">=</span> head<span class="token punctuation">.</span>offsetTop<span class="token punctuation">;</span></span>
<span class="line">        <span class="token keyword">switch</span> <span class="token punctuation">(</span>dir<span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">          <span class="token keyword">case</span> <span class="token string">"ArrowUp"</span><span class="token operator">:</span></span>
<span class="line">            y <span class="token operator">-=</span> <span class="token number">10</span><span class="token punctuation">;</span></span>
<span class="line">            <span class="token keyword">break</span><span class="token punctuation">;</span></span>
<span class="line">          <span class="token keyword">case</span> <span class="token string">"ArrowDown"</span><span class="token operator">:</span></span>
<span class="line">            y <span class="token operator">+=</span> <span class="token number">10</span><span class="token punctuation">;</span></span>
<span class="line">            <span class="token keyword">break</span><span class="token punctuation">;</span></span>
<span class="line">          <span class="token keyword">case</span> <span class="token string">"ArrowLeft"</span><span class="token operator">:</span></span>
<span class="line">            x <span class="token operator">-=</span> <span class="token number">10</span><span class="token punctuation">;</span></span>
