<span class="line">            <span class="token comment">//向右移动蛇</span></span>
<span class="line">            x <span class="token operator">+=</span> <span class="token number">10</span><span class="token punctuation">;</span></span>
<span class="line">            <span class="token keyword">break</span><span class="token punctuation">;</span></span>
<span class="line">        <span class="token punctuation">}</span></span>
<span class="line">        <span class="token keyword">if</span> <span class="token punctuation">(</span>head<span class="token punctuation">.</span>offsetTop <span class="token operator">===</span> food<span class="token punctuation">.</span>offsetTop <span class="token operator">&amp;&amp;</span> head<span class="token punctuation">.</span>offsetLeft <span class="token operator">===</span> food<span class="token punctuation">.</span>offsetLeft<span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">          console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token string">"吃到食物了"</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
<span class="line">          <span class="token comment">//1.改变食物的位置</span></span>
<span class="line">          <span class="token function">changeFood</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
