<span class="line">          snake<span class="token punctuation">.</span><span class="token function">insertAdjacentHTML</span><span class="token punctuation">(</span><span class="token string">"beforeend"</span><span class="token punctuation">,</span> <span class="token string">"&lt;div/>"</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
