<span class="line">        <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>script</span><span class="token punctuation">></span></span><span class="token script"><span class="token language-javascript"></span>
<span class="line">            <span class="token function">$</span><span class="token punctuation">(</span>document<span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">ready</span><span class="token punctuation">(</span><span class="token keyword">function</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">                <span class="token comment">/*通过Ajax请求外部数据*/</span></span>
<span class="line">                $<span class="token punctuation">.</span><span class="token function">ajax</span><span class="token punctuation">(</span><span class="token punctuation">{</span></span>
<span class="line">                    <span class="token literal-property property">url</span><span class="token operator">:</span> <span class="token string">"data.json"</span><span class="token punctuation">,</span> <span class="token comment">//发送请求的地址</span></span>
<span class="line">                    <span class="token comment">// dataType:"json"    //返回的数据类型</span></span>
<span class="line">                    <span class="token function-variable function">success</span><span class="token operator">:</span> <span class="token keyword">function</span> <span class="token punctuation">(</span><span class="token parameter">data</span><span class="token punctuation">)</span> <span class="token punctuation">{</span></span>
<span class="line">                        <span class="token comment">//当请求成功时运行的函数</span></span>
<span class="line"></span>
<span class="line">                        <span class="token comment">// console.log(data);   //{}</span></span>
<span class="line">                        <span class="token comment">// //数据显示到页面上</span></span>
